definition.script:
node {

    sh 'env'

    git branch: 'docker-registry-jenkins', url: 'https://github.com/g0t4/docker-mongo-sample-datasets'
    sh 'docker version'

    image_tag="ci-${BUILD_NUMBER}"
    repo_name="mongo-with-data"
    registry="learning-registry:5000"
    repo_full="${registry}/${repo_name}"
    image="${repo_full}:${image_tag}"

    // note: errors might mean you didn't push mongo to vetted-registry and/or pushed something that's not mongo 
    // note: docker build is cached if nothing changes 
    sh "docker build -t ${image} ."

    // optional: show repos before push:
    sh "curl -s http://${registry}/v2/_catalog"
    sh "curl -s http://${registry}/v2/${repo_name}/tags/list"

    sh "docker push ${image}"

    // optional: show repos after push:
    sh "curl -s http://${registry}/v2/_catalog"
    sh "curl -s http://${registry}/v2/${repo_name}/tags/list"

}
